mapper.update:

update t_patent_property
set value = replace(value,#{value,jdbcType=VARCHAR},'')
where patent_id = #{patentId,jdbcType=INTEGER} and sort_id = #{sortId,jdbcType=INTEGER}

mapper.select:
select patent_id,sort_id, `value` from t_patent_property
where patent_id = (
select tpp.patent_id as patent_id from t_patent_property tpp
left join t_patent tp on tp.id = tpp.patent_id
where tpp.sort_id = #{sortId,jdbcType=INTEGER} and tpp.`value` = #{value,jdbcType=VARCHAR}
and tp.template_id = #{templateId,jdbcType=INTEGER} limit 0,1
) and `value` != '' and sort_id in

  #{item,jdbcType=INTEGER}

select ttp. name as name from t_patent tp
  left join t_template_property ttp on tp.template_id = ttp.template_id and ttp.sort_id = #{sortId,jdbcType=INTEGER}
where tp.id = #{id,jdbcType=INTEGER}

select ttp.value from t_patent tp
  left join t_template_property ttp on tp.template_id = ttp.template_id and ttp.sort_id = #{sortId,jdbcType=INTEGER}
where tp.id = #{id,jdbcType=INTEGER}
